        address newOwner,
        address _bankOracle
    ) FiatTokenV2(tokenName, tokenSymbol, tokenCurrency, tokenDecimals) {
        _setMasterMinter(newMasterMinter);
        _setPauser(newPauser);
        _setBlacklister(newBlacklister);
        _transferOwnership(newOwner);
        bankOracle = _bankOracle;
    }

    modifier hasBacking(uint256 amount) {
        uint256 backing = IBankOracle(bankOracle).getBalance(address(this));
        require(totalSupply() + amount <= backing, "Insufficient bank backing");
        _;
    }

    function mint(address _to, uint256 _amount)
        public
        override
        whenNotPaused
        onlyMinters
        hasBacking(_amount)
        returns (bool)
    {
        return super.mint(_to, _amount);
    }
}

interface IBankOracle {
    function getBalance(address token) external view returns (uint256);
}

contracts/BankOracle.sol.content:
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";

contract BankOracle is Ownable {
    struct BankAccount {
        string pluggyAccountId;
        uint256 balanceBRL;
        uint256 lastUpdated;
        bool active;
    }

    mapping(address => BankAccount) public tokenToAccount;
    mapping(address => bool) public authorizedUpdaters;

    event BalanceUpdated(address indexed token, uint256 oldBalance, uint256 newBalance, uint256 timestamp);
    event AccountLinked(address indexed token, string pluggyAccountId);

    modifier onlyUpdater() {
        require(authorizedUpdaters[msg.sender], "Not authorized");
        _;
    }

    constructor() {
        authorizedUpdaters[msg.sender] = true;
    }

    function updateBalance(address token, string memory accountId, uint256 newBalanceBRL) external onlyUpdater {
        BankAccount storage account = tokenToAccount[token];
        require(account.active, "Account not active");

        uint256 oldBalance = account.balanceBRL;
        account.balanceBRL = newBalanceBRL;
        account.lastUpdated = block.timestamp;

        emit BalanceUpdated(token, oldBalance, newBalanceBRL, block.timestamp);
    }

    function linkAccount(address token, string memory accountId) external onlyOwner {
        tokenToAccount[token] = BankAccount({
            pluggyAccountId: accountId,
            balanceBRL: 0,
            lastUpdated: 0,
            active: true
        });
        emit AccountLinked(token, accountId);
    }

    function getBalance(address token) external view returns (uint256) {
        return tokenToAccount[token].balanceBRL;
    }

    function authorizeUpdater(address updater, bool authorized) external onlyOwner {
        authorizedUpdaters[updater] = authorized;
    }
}

contracts/FiatTokenV2.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

contract FiatTokenV2 is ERC20, Ownable, Pausable {
    string private _name;
    string private _symbol;
    uint8 private _decimals;
    string private _currency;

    address public masterMinter;
    address public pauser;
    address public blacklister;

    mapping(address => bool) public minters;
    mapping(address => uint256) public minterAllowance;
    mapping(address => bool) public blacklisted;

    event Mint(address indexed minter, address indexed to, uint256 amount);
    event Burn(address indexed burner, uint256 amount);
    event MinterConfigured(address indexed minter, uint256 minterAllowedAmount);
    event MinterRemoved(address indexed oldMinter);
    event MasterMinterChanged(address indexed newMasterMinter);
    event PauserChanged(address indexed newPauser);
    event BlacklisterChanged(address indexed newBlacklister);
    event Blacklisted(address indexed account);
    event UnBlacklisted(address indexed account);

    constructor(
        string memory tokenName,
        string memory tokenSymbol,
        string memory tokenCurrency,
        uint8 tokenDecimals
    ) ERC20(tokenName, tokenSymbol) {
        _name = tokenName;
        _symbol = tokenSymbol;
        _currency = tokenCurrency;
        _decimals = tokenDecimals;
    }

    modifier onlyMasterMinter() {
        require(msg.sender == masterMinter, "FiatToken: caller is not the masterMinter");
        _;
    }

    modifier onlyMinters() {
        require(minters[msg.sender], "FiatToken: caller is not a minter");
        _;
    }

    modifier onlyPauser() {
        require(msg.sender == pauser, "FiatToken: caller is not the pauser");
        _;
    }

    modifier onlyBlacklister() {
        require(msg.sender == blacklister, "FiatToken: caller is not the blacklister");
        _;
    }

    modifier notBlacklisted(address account) {
        require(!blacklisted[account], "FiatToken: account is blacklisted");
        _;
    }

    function name() public view override returns (string memory) {
        return _name;
    }

    function symbol() public view override returns (string memory) {
        return _symbol;
    }

    function decimals() public view override returns (uint8) {
        return _decimals;
    }

    function currency() public view returns (string memory) {
        return _currency;
    }

    function mint(address _to, uint256 _amount) public virtual whenNotPaused onlyMinters notBlacklisted(_to) returns (bool) {
        require(_to != address(0), "FiatToken: mint to the zero address");
        require(_amount > 0, "FiatToken: mint amount not greater than 0");

        uint256 mintingAllowedAmount = minterAllowance[msg.sender];
        require(_amount <= mintingAllowedAmount, "FiatToken: mint amount exceeds minterAllowance");

        minterAllowance[msg.sender] = mintingAllowedAmount - _amount;
        _mint(_to, _amount);
        emit Mint(msg.sender, _to, _amount);
        return true;
    }

    function burn(uint256 _amount) public whenNotPaused onlyMinters notBlacklisted(msg.sender) {
        require(_amount > 0, "FiatToken: burn amount not greater than 0");

        _burn(msg.sender, _amount);
        emit Burn(msg.sender, _amount);
    }

    function transfer(address recipient, uint256 amount) public override whenNotPaused notBlacklisted(msg.sender) notBlacklisted(recipient) returns (bool) {
        return super.transfer(recipient, amount);
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override whenNotPaused notBlacklisted(sender) notBlacklisted(recipient) returns (bool) {
        return super.transferFrom(sender, recipient, amount);
    }

    function configureMinter(address minter, uint256 minterAllowedAmount) public onlyMasterMinter returns (bool) {
        minters[minter] = true;
        minterAllowance[minter] = minterAllowedAmount;
        emit MinterConfigured(minter, minterAllowedAmount);
        return true;
    }

    function removeMinter(address minter) public onlyMasterMinter returns (bool) {
        minters[minter] = false;
        minterAllowance[minter] = 0;
        emit MinterRemoved(minter);
        return true;
    }

    function _setMasterMinter(address newMasterMinter) internal {
        masterMinter = newMasterMinter;
        emit MasterMinterChanged(newMasterMinter);
    }

    function _setPauser(address newPauser) internal {
        pauser = newPauser;
        emit PauserChanged(newPauser);
    }

    function _setBlacklister(address newBlacklister) internal {
        blacklister = newBlacklister;
        emit BlacklisterChanged(newBlacklister);
    }

    function pause() public onlyPauser {
        _pause();
    }

    function unpause() public onlyPauser {
        _unpause();
    }

    function blacklist(address account) public onlyBlacklister {
        blacklisted[account] = true;
        emit Blacklisted(account);
    }

    function unBlacklist(address account) public onlyBlacklister {
        blacklisted[account] = false;
        emit UnBlacklisted(account);
    }
}

contracts/TokenFactory.sol.content:
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract TokenFactory {
    address public immutable tokenTemplate;
    address public immutable bankOracle;
    address public proxyAdmin;

    struct TokenDeployment {
        address tokenAddress;
        address proxyAddress;
        string name;
        string symbol;
        address deployer;
        uint256 deployedAt;
    }

    TokenDeployment[] public deployments;

    event TokenDeployed(
        address indexed token,
        address indexed proxy,
        address indexed deployer,
        string name,
        string symbol
    );

    constructor(address _tokenTemplate, address _bankOracle, address _proxyAdmin) {
        tokenTemplate = _tokenTemplate;
        bankOracle = _bankOracle;
        proxyAdmin = _proxyAdmin;
    }

    function deployToken(
        string memory name,
        string memory symbol,
        address masterMinter,
        address pauser,
        address blacklister,
        address owner
    ) external returns (address tokenProxy, address implementation) {
        implementation = Clones.clone(tokenTemplate);

        bytes memory proxyInitCode = abi.encodePacked(
            type(FiatTokenProxy).creationCode,
            abi.encode(implementation)
        );

        bytes32 salt = keccak256(abi.encodePacked(name, symbol, block.timestamp));

        assembly {
            tokenProxy := create2(0, add(proxyInitCode, 0x20), mload(proxyInitCode), salt)
        }

        IBankBackedToken(tokenProxy).initializeBankBacked(
            name,
            symbol,
            "BRL",
            2,
            masterMinter,
            pauser,
            blacklister,
            owner,
            bankOracle
        );

        IFiatTokenProxy(tokenProxy).changeAdmin(proxyAdmin);

        deployments.push(TokenDeployment({
            tokenAddress: implementation,
            proxyAddress: tokenProxy,
            name: name,
            symbol: symbol,
            deployer: msg.sender,
            deployedAt: block.timestamp
        }));

        emit TokenDeployed(tokenProxy, implementation, msg.sender, name, symbol);

        return (tokenProxy, implementation);
    }
}

interface IBankBackedToken {
    function initializeBankBacked(
        string memory tokenName,
        string memory tokenSymbol,
        string memory tokenCurrency,
        uint8 tokenDecimals,
        address newMasterMinter,
        address newPauser,
        address newBlacklister,
        address newOwner,
        address _bankOracle
    ) external;
}

interface IFiatTokenProxy {
    function changeAdmin(address newAdmin) external;
}

contract FiatTokenProxy {
    address public implementation;
    address public admin;

    constructor(address _implementation) {
        implementation = _implementation;
        admin = msg.sender;
    }

    function changeAdmin(address newAdmin) external {
        require(msg.sender == admin, "Only admin can change admin");
        admin = newAdmin;
    }

    fallback() external payable {
        address impl = implementation;
        assembly {
            let ptr := mload(0x40)
            calldatacopy(ptr, 0, calldatasize())
            let result := delegatecall(gas(), impl, ptr, calldatasize(), 0, 0)
            let size := returndatasize()
            returndatacopy(ptr, 0, size)

            switch result
            case 0 { revert(ptr, size) }
            default { return(ptr, size) }
        }
    }

    receive() external payable {}
}

